row.Value:
	Приемник.Валюта = Приемник.xmlВалюта;
	Приемник.Записать();

	Запрос = Новый Запрос;
	Запрос.Текст = 
		"ВЫБРАТЬ
		|	НоменклатураСправочник.Ссылка КАК Номенклатура,
		|	НоменклатураСправочник.БазоваяЕдиницаИзмерения.ЕдиницаПоКлассификатору КАК ЕдиницаПоКлассификатору,
		|	ЭКОМ_ДокументыТЧ_Товары.НомерПродуктаПокупателя
		|ИЗ
		|	Документ.ЭКОМ_Документы.ТЧ_Товары КАК ЭКОМ_ДокументыТЧ_Товары
		|		ЛЕВОЕ СОЕДИНЕНИЕ Справочник.Номенклатура КАК НоменклатураСправочник
		|		ПО (ЭКОМ_ДокументыТЧ_Товары.НомерПродуктаПокупателя = ПОДСТРОКА(НоменклатураСправочник.Код, 0, 150))
		|ГДЕ
		|	ЭКОМ_ДокументыТЧ_Товары.Ссылка В(&Документ)";
    Запрос.УстановитьПараметр("Документ", Приемник.Ссылка);
	Результат = Запрос.Выполнить();
	ТЗ = Результат.Выгрузить();
    ТЗ.Индексы.Добавить("НомерПродуктаПокупателя");

	Для Каждого Стр Из Приемник.ТЧ_Товары Цикл
		НайденнаяСтрока = ТЗ.Найти(Стр.НомерПродуктаПокупателя, "НомерПродуктаПокупателя");	
		Если НЕ НайденнаяСтрока = Неопределено Тогда  
			Стр.Номенклатура     = НайденнаяСтрока.Номенклатура;
			Стр.ЕдиницаИзмерения = НайденнаяСтрока.ЕдиницаПоКлассификатору;
		КонецЕсли;          
	КонецЦикла;

	Запрос = Новый Запрос;
	Запрос.Текст = 
		"ВЫБРАТЬ
		|	Заказ.Ссылка
		|ИЗ
		|	Документ.ЭКОМ_Документы КАК ЭКОМ_Документы
		|		ЛЕВОЕ СОЕДИНЕНИЕ Документ.Заказ КАК Заказ
		|		ПО ЭКОМ_Документы.ЗаказНомер = Заказ.Номер
		|			И (НАЧАЛОПЕРИОДА(ЭКОМ_Документы.ЗаказДата, ДЕНЬ) = НАЧАЛОПЕРИОДА(ЭКОМ_Документы.Дата, ДЕНЬ))
		|ГДЕ
		|	ЭКОМ_Документы.Ссылка = &Ссылка
		|	И НЕ Заказ.ПометкаУдаления";
    Запрос.УстановитьПараметр("Ссылка", Приемник.Ссылка);
	Результат = Запрос.Выполнить();
	Если НЕ Результат.Пустой() Тогда
		Выборка = Результат.Выбрать();
		Выборка.Следующий();
		Приемник.Документ1С = Выборка.Ссылка;
	КонецЕсли;	

	//Если ЗначениеЗаполнено(Приемник.Документ1С) Тогда
	//	ЗаказОбъект = Приемник.Документ1С.ПолучитьОбъект();
		//ЗаказОбъект.Подтвержен = Истина;
	//	ЗаказОбъект.Записать();
	//КонецЕсли;
