Для Каждого Строка Из Результат Цикл

	Если ПустаяСтрока(Строка.PRODUCT) Тогда
	    Сообщить("У номенклатуры " + Строка.НаименованиеНоменклатуры 
		+ "не указано соответствие номенклатуры контрагента или не заполнен штрихкод"  + Строка.КонтрагентНаименование);
		Продолжить;		
	КонецЕсли;

	Если ЗначениеЗаполнено(Строка.НаименованиеНоменклатурыКонтрагента) Тогда
		Строка.DESCRIPTION = Строка.НаименованиеНоменклатурыКонтрагента;
	Иначе
		Строка.DESCRIPTION = Строка.НаименованиеНоменклатуры;
	КонецЕсли; 

	Строка.PRODUCTIDBUYER = Строка.КодНоменклатурыКонтрагента;

    Строка.DELIVEREDUNIT = ПолучитьЕдиницуИзмерения(Строка.ЕдиницаИзмеренияНаименование);
  	Строка.ORDEREDQUANTITY 	= Формат(Строка.КоличествоЗаказанное, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Строка.DELIVEREDQUANTITY = Формат(Строка.КоличествоПоставляемое, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

    СтавкаНДС = СтавкаНДСЧислом(Строка.СтавкаНДС);

    ФорматЦена = Формат(Строка.Цена, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

	Если Строка.СуммаВключаетНДС Тогда
	    ЦенаСНДС 	= ФорматЦена;
		ЦенаБезНДС 	= Формат(Строка.Цена/((100 + СтавкаНДС)/100), "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
		СуммаСНДС = Формат(Строка.Сумма, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
		СуммаБезНДС = Формат(Строка.Сумма/((100 + СтавкаНДС)/100), "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе	
	    ЦенаБезНДС 	= ФорматЦена;
		ЦенаСНДС 	= Формат(Строка.Цена * ((100 + СтавкаНДС)/100), "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
		СуммаБезНДС = Формат(Строка.Сумма, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
		СуммаСНДС = Формат(Строка.Сумма * ((100 + СтавкаНДС)/100), "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	КонецЕсли;

    Строка.TAXRATE 			= СтавкаНДС;
	Строка.PRICE 			= ЦенаБезНДС;
	Строка.PRICEWITHVAT 	= ЦенаСНДС;
	Строка.AMOUNT 			= СуммаБезНДС;
	Строка.AMOUNTWITHVAT 	= СуммаСНДС;
	Строка.TAXAMOUNT 		= Формат(Строка.СуммаНДС, "ЧДЦ=2; ЧРД=.; ЧН=0; ЧГ=0");

