СтрокиДляУдаления = Новый Массив;

Для Каждого Строка Из Результат Цикл

	EDIINTERCHANGEID = НовыйНомерТранзакции(EDIINTERCHANGEID);

	НомерБезПрефикса = ПрефиксацияОбъектовКлиентСервер.НомерНаПечать(Строка.Номер , Истина, Истина);

	Строка.NUMBER = СокрЛП(НомерБезПрефикса);
	Строка.DATE 		= Формат(Строка.Дата, "ДФ=""гггг-ММ-дд""");
	Строка.WAYBILLNUMBER	= СокрЛП(НомерБезПрефикса);
	Строка.WAYBILLDATE		= Формат(Строка.Дата, "ДФ=""гггг-ММ-дд""");
	Строка.DELIVERYDATE = Формат(Строка.ДатаПоставки, "ДФ=""гггг-ММ-дд""");
	Строка.DELIVERYTIME = Формат(Строка.ДатаПоставки, "ДФ=""HH:mm""");
	Строка.ORDERNUMBER 	= СокрЛП(Строка.ЗаказНомер);	
	Строка.ORDERDATE = Формат(Строка.ЗаказДата, "ДФ=""гггг-ММ-дд""");	
	Строка.DELIVERYNOTENUMBER = СокрЛП(НомерБезПрефикса);
	Строка.DELIVERYNOTEDATE	  = Формат(Строка.Дата, "ДФ=""гггг-ММ-дд""");
	Строка.CAMPAIGNNUMBER = СокрЛП(Строка.НомерДоговораНаПоставку);

	Если НЕ ЗначениеЗаполнено(Строка.СчетФактура) Тогда

		Если Строка.ФорматУПД = "УПД Х5" ИЛИ  
			Строка.ФорматУПД = "УПД 7 шагов" Тогда

			Сообщить( "Для отправки DESADV необходимо создать документ СчетФактураВыданный для док.<" + Строка.Ссылка + ">", СтатусСообщения.Важное );
			СтрокиДляУдаления.Добавить(Строка);

		КонецЕсли;

	Иначе
		Строка.INVOICENUMBER = СокрЛП(Строка.СчетФактураНомер);
		Строка.INVOICEDATE = Формат(Строка.СчетФактураДата, "ДФ=""гггг-ММ-дд""");
	КонецЕсли;

	Строка.TRANSPORTTYPE = "30";
	Строка.TRANSPORTERTYPE = "30"; 
	Строка.SHIPMENTS = "1"; 	
	Строка.TRANSPORTQUANTITY = ?(ЗначениеЗаполнено(Строка.TRANSPORTQUANTITY), Строка.TRANSPORTQUANTITY, 1);

	Если ЗначениеЗаполнено(Строка.TRANSPORTERPHONEРабочий) Тогда
		 Строка.TRANSPORTERPHONE = Строка.TRANSPORTERPHONEРабочий;
	Иначе
	     Строка.TRANSPORTERPHONE = Строка.TRANSPORTERPHONEДомашний;
	КонецЕсли;

	Строка.EDIINTERCHANGEID = Формат(EDIINTERCHANGEID, "ЧГ=0");

	СлучайноеЧисло 			= ГСЧ.СлучайноеЧисло(100, 999);
	Строка.EDIMESSAGE       = Формат(ТекущаяДата(), "ДФ=ггггММддЧЧммсс") + СлучайноеЧисло + "-OUT-" + Строка.EDIINTERCHANGEID;

	Строка.ФайлИмя 			= "DESADV" + "_" + Строка.EDIMESSAGE + ".xml";
	Строка.Идентификатор 	= "DESADV" + "_" + Строка.EDIMESSAGE;

	/////////////////////////////////////////////////////////////////////////////////////////
// Регистрация в цепочке документов
НаборЗаписей = РегистрыСведений.ЭКОМ_ЦепочкиДокументовПоставщика.СоздатьНаборЗаписей();
НаборЗаписей.Отбор.Накладная.Установить(Строка.Ссылка);

НаборЗаписей.Прочитать();

// Если существует запись в регистре
Для Каждого НоваяЗапись Из НаборЗаписей Цикл

	НоваяЗапись.DESADVNUMBER = Строка.NUMBER;
	НоваяЗапись.DESADVDATE = Строка.Дата;

	НаборЗаписей.Записать();

КонецЦикла;	

// Запись не найдена в цепочке
Если НаборЗаписей.Количество() = 0 Тогда
	// Сообщать только для тестирования 
	//Сообщить("Не найден документ ЭКОМ(ORDER) или не все измерения заполнены для регистра сведений ""ЭКОМ_ЦепочкиДокументовПоставщика""", СтатусСообщения.ОченьВажное);			
КонецЕсли;
///////////////////////////////////////////////////////////////////////////////////////////////////


КонецЦикла;

Для Каждого Строка Из СтрокиДляУдаления Цикл
	Результат.Удалить(Строка);
КонецЦикла;

Возврат Результат;
