		Строка.PACKINGTYPE = Позиция.PACKINGTYPE;
		Строка.PACKINGQUANTITY = Позиция.PACKINGQUANTITY;
		Строка.PACKINGUNIT = Позиция.PACKINGUNIT;
		Прервать;
		КонецЕсли
	КонецЦикла;

