Для Каждого Строка Из Результат Цикл

	Строка.WEIGHTNETTO = Формат(Строка.ВесНеттоПалеты, "ЧДЦ=3; ЧРД=.; ЧГ=0");
	Строка.WEIGHTBRUTTO = Формат(Строка.ВесБруттоПалеты, "ЧДЦ=3; ЧРД=.; ЧГ=0");
	Строка.HEIGHT = Формат(Строка.Высота, "ЧГ=0");
	Строка.VOLUME = Формат(Строка.Объем, "ЧГ=0");
	Строка.HIERARCHICALID = Строка.НомерПалета;
	Строка.MANUFACTUREDATE = Формат(Строка.ДатаПроизводства, "ДФ=yyyy-MM-dd");
	Строка.SHELFLIFEDATE = Формат(Строка.ДатаПроизводства, "ДФ=yyyy-MM-dd");
	Строка.EXPIREDATE = Формат(Строка.ДатаОкончанияСрокаГодности, "ДФ=yyyy-MM-dd");
	Если ЗначениеЗаполнено(Строка.СрокХраненияВДнях) Тогда
		Строка.VARBESTDATE = Формат(Строка.СрокХраненияВДнях, "ЧДЦ=0; ЧГ=0");
	Иначе
		СрокХраненияВДнях = Цел( (Строка.ДатаОкончанияСрокаГодности - Строка.ДатаПроизводства) / 86400 );
		Строка.VARBESTDATE = Формат(СрокХраненияВДнях, "ЧДЦ=0; ЧГ=0");
	КонецЕсли;

КонецЦикла;

Возврат Результат;

