	ФорматСумма = "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0";

	Если ПустаяСтрока(Строка.PRODUCT) Тогда
		Сообщить("У номенклатуры " + Строка.НоменклатураНаименование 
			+ " не указано соответствие номенклатуры контрагента или не заполнен штрих код, контрагент: " + Строка.КонтрагентНаименование);
	КонецЕсли;

	Строка.INVOICEDQUANTITY = Формат(Строка.КоличествоПоставляемое, "ЧДЦ=3; ЧРД=.; ЧН=0; ЧГ=0");
	Строка.INVOICEUNIT = СокрЛП(Строка.ЕдиницаИзмеренияИзORDER);

	СтавкаНДС = Строка.СтавкаНДС;

	Если Строка.СуммаВключаетНДС Тогда
		ЦенаБезНДС = Формат(Строка.Цена / ((100 + СтавкаНДС) / 100), ФорматСумма);
		ЦенаСНДС = Формат(Строка.Цена, ФорматСумма);
		СуммаБезНДС = Формат(Строка.Сумма / ((100 + СтавкаНДС) / 100), ФорматСумма);
		СуммаСНДС = Формат(Строка.Сумма, ФорматСумма);
	Иначе
		ЦенаБезНДС = Формат(Строка.Цена, ФорматСумма);
		ЦенаСНДС = Формат(Строка.Цена * ((100 + СтавкаНДС) / 100), ФорматСумма);
		СуммаБезНДС = Формат(Строка.Сумма, ФорматСумма);
		СуммаСНДС = Формат(Строка.Сумма * ((100 + СтавкаНДС) / 100), ФорматСумма);
	КонецЕсли;

	Строка.UNITPRICE		= ЦенаБезНДС;
	Строка.GROSSPRICE		= ЦенаСНДС;
	Строка.AMOUNT			= СуммаБезНДС;
	Строка.AMOUNTWITHVAT	= СуммаСНДС;
	Строка.TAXAMOUNT		= Формат(Строка.СуммаНДС, "ЧДЦ=2; ЧРД=.; ЧН=0; ЧГ=0");

КонецЦикла;

