row.Value:
Результат  = Новый ТаблицаЗначений;

Колонки = Новый Массив;
Колонки.Добавить("Ссылка");
Колонки.Добавить("ДанныеПоПаллетам");

Для Каждого Колонка Из Колонки Цикл
	Результат.Колонки.Добавить(Колонка);
КонецЦикла;

Запрос = Новый Запрос;
Запрос.Текст = "ВЫБРАТЬ РАЗЛИЧНЫЕ
               |	ЭКОМ_Палеты.Документ КАК Ссылка,
               |	ЭКОМ_Палеты.SSCC КАК SSCC,
               |	ЭКОМ_Палеты.ВесБрутто КАК МассаБрутто
               |ИЗ
               |	РегистрСведений.ЭКОМ_Палеты КАК ЭКОМ_Палеты
               |ГДЕ
               |	ЭКОМ_Палеты.Документ В(&Документ)
               |ИТОГИ ПО
               |	Ссылка";

Запрос.УстановитьПараметр("Документ", Источник);
РезультатЗапроса = Запрос.Выполнить();
ВыборкаСсылка = РезультатЗапроса.Выбрать(ОбходРезультатаЗапроса.ПоГруппировкам, "Ссылка");

Пока ВыборкаСсылка.Следующий() Цикл
	ДанныеПоПаллетам = Новый СписокЗначений;

	Выборка = ВыборкаСсылка.Выбрать();
	Пока Выборка.Следующий() Цикл

		СтрокаСтруктуры = Новый Структура;
		СтрокаСтруктуры.Вставить("SSCC"       , Выборка.SSCC);
		СтрокаСтруктуры.Вставить("МассаБрутто", Формат(Выборка.МассаБрутто, "ЧДЦ=3; ЧРД=.; ЧГ=0; ЧН="));

		ДанныеПоПаллетам.Добавить(СтрокаСтруктуры);	

	КонецЦикла;

	Строка = Результат.Добавить();
    Строка.Ссылка = ВыборкаСсылка.Ссылка;
    Строка.ДанныеПоПаллетам = ДанныеПоПаллетам;

КонецЦикла;

Возврат Результат;

