	СтруктураДокумента.ГрузПеревоз_Район        = ВыборкаДокумента.ГрузПеревоз_Район;
	СтруктураДокумента.ГрузПеревоз_Город        = ВыборкаДокумента.ГрузПеревоз_Город;
	СтруктураДокумента.ГрузПеревоз_Улица        = ВыборкаДокумента.ГрузПеревоз_Улица;
	СтруктураДокумента.ГрузПеревоз_Дом          = ВыборкаДокумента.ГрузПеревоз_Дом;
	СтруктураДокумента.ГрузПеревоз_Корпус       = ВыборкаДокумента.ГрузПеревоз_Корпус;
	СтруктураДокумента.ГрузПеревоз_НомерТел     = ВыборкаДокумента.ГрузПеревоз_НомерТел;

	//ГрузПеревозЗамен
	СтруктураДокумента.ГрузПеревозЗамен_Наименование = ВыборкаДокумента.ГрузПеревозЗамен_Наименование;
	СтруктураДокумента.ГрузПеревозЗамен_ИНН          = ВыборкаДокумента.ГрузПеревозЗамен_ИНН;
	СтруктураДокумента.ГрузПеревозЗамен_КПП          = ВыборкаДокумента.ГрузПеревозЗамен_КПП;
	СтруктураДокумента.ГрузПеревозЗамен_Индекс       = ВыборкаДокумента.ГрузПеревозЗамен_Индекс;
	СтруктураДокумента.ГрузПеревозЗамен_КодРегион    = ВыборкаДокумента.ГрузПеревозЗамен_КодРегион;
	СтруктураДокумента.ГрузПеревозЗамен_Район        = ВыборкаДокумента.ГрузПеревозЗамен_Район;
	СтруктураДокумента.ГрузПеревозЗамен_Город        = ВыборкаДокумента.ГрузПеревозЗамен_Город;
	СтруктураДокумента.ГрузПеревозЗамен_Улица        = ВыборкаДокумента.ГрузПеревозЗамен_Улица;
	СтруктураДокумента.ГрузПеревозЗамен_Дом          = ВыборкаДокумента.ГрузПеревозЗамен_Дом;
	СтруктураДокумента.ГрузПеревозЗамен_Корпус       = ВыборкаДокумента.ГрузПеревозЗамен_Корпус;
	СтруктураДокумента.ГрузПеревозЗамен_НомерТел     = ВыборкаДокумента.ГрузПеревозЗамен_НомерТел;

	//Плательщик
	СтруктураДокумента.Плательщик_Наименование = ВыборкаДокумента.Плательщик_Наименование;
	СтруктураДокумента.Плательщик_ИНН          = ВыборкаДокумента.Плательщик_ИНН;
	СтруктураДокумента.Плательщик_КПП          = ВыборкаДокумента.Плательщик_КПП;
	СтруктураДокумента.Плательщик_Индекс       = ВыборкаДокумента.Плательщик_Индекс;
	СтруктураДокумента.Плательщик_КодРегион    = ВыборкаДокумента.Плательщик_КодРегион;
	СтруктураДокумента.Плательщик_Район        = ВыборкаДокумента.Плательщик_Район;
	СтруктураДокумента.Плательщик_Город        = ВыборкаДокумента.Плательщик_Город;
	СтруктураДокумента.Плательщик_Улица        = ВыборкаДокумента.Плательщик_Улица;
	СтруктураДокумента.Плательщик_Дом          = ВыборкаДокумента.Плательщик_Дом;
	СтруктураДокумента.Плательщик_Корпус       = ВыборкаДокумента.Плательщик_Корпус;
	СтруктураДокумента.Плательщик_РасчетСчет   = ВыборкаДокумента.Плательщик_РасчетСчет;
	СтруктураДокумента.Плательщик_НаименБанка  = ВыборкаДокумента.Плательщик_НаименБанка;
	СтруктураДокумента.Плательщик_БИК          = ВыборкаДокумента.Плательщик_БИК;
	СтруктураДокумента.Плательщик_КоррСчет     = ВыборкаДокумента.Плательщик_КоррСчет;

	Если ЗначениеЗаполнено(ВыборкаДокумента.Кладовщик_Фамилия) Тогда

		ДлинаСтроки = СтрДлина(ВыборкаДокумента.Кладовщик_Фамилия);
		ПервыйСимволПробела = Найти(ВыборкаДокумента.Кладовщик_Фамилия, " ");

		ИмяОтчество = Сред(ВыборкаДокумента.Кладовщик_Фамилия, ПервыйСимволПробела + 1, ДлинаСтроки-ПервыйСимволПробела);
		ВторойСимволПробела = Найти(ИмяОтчество, " ");

		Если ВторойСимволПробела = 0 Тогда
			 КладовщикФамилия  = Лев(ВыборкаДокумента.Кладовщик_Фамилия, ПервыйСимволПробела-1);
		     КладовщикИмя      = Прав(ИмяОтчество, ДлинаСтроки - ВторойСимволПробела - ПервыйСимволПробела);
		     КладовщикОтчество = "";
		Иначе
			 КладовщикФамилия  = Лев(ВыборкаДокумента.Кладовщик_Фамилия, ПервыйСимволПробела-1);
		     КладовщикИмя      = Лев(ИмяОтчество, ВторойСимволПробела-1);
		     КладовщикОтчество = Прав(ИмяОтчество, ДлинаСтроки - ВторойСимволПробела - ПервыйСимволПробела);
		КонецЕсли;
	Иначе
		КладовщикФамилия  = ВыборкаДокумента.Кладовщик_Фамилия;
		КладовщикИмя      = ВыборкаДокумента.Кладовщик_Имя;
		КладовщикОтчество = ВыборкаДокумента.Кладовщик_Отчество;
	КонецЕсли;

	// Кладовщик
	СтруктураДокумента.Кладовщик_Должность = ВыборкаДокумента.Кладовщик_Должность;
	СтруктураДокумента.Кладовщик_Фамилия   = КладовщикФамилия;
	СтруктураДокумента.Кладовщик_Имя       = КладовщикИмя;
	СтруктураДокумента.Кладовщик_Отчество  = КладовщикОтчество;
	СтруктураДокумента.Кладовщик_Дата      = ДатаПодписи;
	СтруктураДокумента.Кладовщик_Время     = ВремяПодписи;

	// ПредставительГО
	СтруктураДокумента.ПредставительГО_Должность = ВыборкаДокумента.ПредставительГО_Должность;
	СтруктураДокумента.ПредставительГО_Фамилия   = КладовщикФамилия;
	СтруктураДокумента.ПредставительГО_Имя       = КладовщикИмя;
	СтруктураДокумента.ПредставительГО_Отчество  = КладовщикОтчество;

	// Водитель
	СтруктураДокумента.Водитель_ИдентифВодит = ВыборкаДокумента.ВодительИдентифВодит;
	СтруктураДокумента.Водитель_Фамилия      = ВыборкаДокумента.ВодительФамилия;
	СтруктураДокумента.Водитель_Имя          = ВыборкаДокумента.ВодительИмя;
	СтруктураДокумента.Водитель_Отчество     = ВыборкаДокумента.ВодительОтчество;
	СтруктураДокумента.Водитель_Телефон      = ВыборкаДокумента.ВодительТелефон;
	СтруктураДокумента.Водитель_ВУ           = ВыборкаДокумента.ВодительВУ;
	СтруктураДокумента.Водитель_Дата         = ДатаПодписи;
	СтруктураДокумента.Водитель_Время        = ВремяПодписи;

КонецЦикла;

Возврат Результат;
