	СтруктураДокумента = Результат.Добавить();	

	Идентификатор = Новый УникальныйИдентификатор;
	ИдентификаторДляФайла = "TRNX5" + "_"
	   + ВыборкаДокумента.ИдентификаторОператораПолучателя + ВыборкаДокумента.ИдГрузПол + "_"
	   + ВыборкаДокумента.ИдентификаторОператораПеревозчика + ВыборкаДокумента.ИдГрузПер + "_"
	   + ВыборкаДокумента.ИдентификаторОператораОтправителя + ВыборкаДокумента.ИдГрузОтпр + "_" 
	   + Формат(ТекущаяДата(), "ДФ=yyyyMMdd") + "_" 
	   + Строка(Идентификатор);

	//Сведения для регистра ЭКОМ события для документа
    СтруктураДокумента.ВидДокумента        = ВыборкаДокумента.ВидДокумента;
    СтруктураДокумента.ЗаказДата           = ВыборкаДокумента.ЗаказДата;
    СтруктураДокумента.ЗаказНомер          = ВыборкаДокумента.ЗаказНомер;
    СтруктураДокумента.Идентификатор       = ИдентификаторДляФайла;
    СтруктураДокумента.ИдентификаторИспр   = ВыборкаДокумента.ИдентификаторИспр;
    СтруктураДокумента.Контрагент          = ВыборкаДокумента.Контрагент;
    СтруктураДокумента.Организация         = ВыборкаДокумента.Организация;
    СтруктураДокумента.Ссылка              = ВыборкаДокумента.Ссылка;
    СтруктураДокумента.Статус              = ВыборкаДокумента.Статус;
    СтруктураДокумента.ФайлИмя             = ИдентификаторДляФайла + ".xml";

	//Файл
    СтруктураДокумента.ИдФайл = ИдентификаторДляФайла;

	//СвУчДокОбор
	СтруктураДокумента.ИдГрузОтпр     = СокрЛП(ВыборкаДокумента.ИдентификаторОператораОтправителя) + СокрЛП(ВыборкаДокумента.ИдГрузОтпр);
	СтруктураДокумента.ИдГрузПол      = СокрЛП(ВыборкаДокумента.ИдентификаторОператораПолучателя) + СокрЛП(ВыборкаДокумента.ИдГрузПол);
	СтруктураДокумента.ИдГрузПер      = СокрЛП(ВыборкаДокумента.ИдентификаторОператораПеревозчика) + СокрЛП(ВыборкаДокумента.ИдГрузПер);

	//СвОЭДОтпр
	СтруктураДокумента.СвОЭДОтпр_НаимОрг = ВыборкаДокумента.СвОЭДОтпр_НаимОрг;
	СтруктураДокумента.СвОЭДОтпр_ИННЮЛ   = ВыборкаДокумента.СвОЭДОтпр_ИННЮЛ;
	СтруктураДокумента.СвОЭДОтпр_ИдЭДО   = ВыборкаДокумента.СвОЭДОтпр_ИдЭДО;

	//Документ
	СтруктураДокумента.Дата  = Формат(ТекущаяДата(), "ДФ=dd.MM.yyyy");
	СтруктураДокумента.Время = Формат(ТекущаяДата(), "ДФ=hh:mm:ss");

	//СвЭТРН
	СтруктураДокумента.ТипЭТРН            = ВыборкаДокумента.ТипЭТРН;
